import React, { useState, useEffect } from 'react';
import { Insights } from '../types/insights';
import { AlertTriangle, Activity, TrendingUp, Database, Clock, Zap } from 'lucide-react';
import AnomaliesTable from './AnomaliesTable';
import CorrelationsPanel from './CorrelationsPanel';
import DriftQualityPanel from './DriftQualityPanel';

interface InsightsDashboardProps {
  insights: Insights | null;
  loading: boolean;
  error: string | null;
}

const InsightsDashboard: React.FC<InsightsDashboardProps> = ({ insights, loading, error }) => {
  const [activeTab, setActiveTab] = useState<'anomalies' | 'correlations' | 'drift'>('anomalies');

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        <span className="ml-3 text-lg">Loading insights...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-4">
        <div className="flex items-center">
          <AlertTriangle className="h-5 w-5 text-red-600 mr-2" />
          <span className="text-red-800">Error loading insights: {error}</span>
        </div>
      </div>
    );
  }

  if (!insights) {
    return (
      <div className="bg-gray-50 border border-gray-200 rounded-lg p-8 text-center">
        <Database className="h-12 w-12 text-gray-400 mx-auto mb-4" />
        <h3 className="text-lg font-medium text-gray-900 mb-2">No Insights Available</h3>
        <p className="text-gray-600">Run the anomaly engine to generate insights.</p>
      </div>
    );
  }

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-blue-100 text-blue-800 border-blue-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getContextColor = (context: string) => {
    switch (context) {
      case 'high_error_environment': return 'text-red-600';
      case 'medium_error_environment': return 'text-orange-600';
      case 'low_medium_error_environment': return 'text-yellow-600';
      case 'low_error_environment': return 'text-green-600';
      default: return 'text-gray-600';
    }
  };

  const formatNumber = (num: number, decimals: number = 0) => {
    return new Intl.NumberFormat('en-US', {
      minimumFractionDigits: decimals,
      maximumFractionDigits: decimals
    }).format(num);
  };

  const formatPercentage = (num: number) => {
    return `${(num * 100).toFixed(1)}%`;
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">Advanced Correlation & Anomaly Engine</h1>
          <div className="flex items-center text-sm text-gray-500">
            <Clock className="h-4 w-4 mr-1" />
            {new Date(insights.timestamp).toLocaleString()}
          </div>
        </div>

        {/* Context Banner */}
        <div className={`p-3 rounded-lg border ${getSeverityColor(insights.severity_context.context_level.includes('high') ? 'critical' : 
          insights.severity_context.context_level.includes('medium') ? 'high' : 'medium')}`}>
          <div className="flex items-center">
            <AlertTriangle className="h-5 w-5 mr-2" />
            <span className="font-medium">{insights.severity_context.context_description}</span>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
          <div className="flex items-center">
            <Database className="h-8 w-8 text-blue-600 mr-3" />
            <div>
              <p className="text-sm font-medium text-gray-600">Total Events</p>
              <p className="text-2xl font-bold text-gray-900">{formatNumber(insights.stats.events)}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
          <div className="flex items-center">
            <Activity className="h-8 w-8 text-green-600 mr-3" />
            <div>
              <p className="text-sm font-medium text-gray-600">Time Series</p>
              <p className="text-2xl font-bold text-gray-900">{formatNumber(insights.stats.series)}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
          <div className="flex items-center">
            <TrendingUp className="h-8 w-8 text-purple-600 mr-3" />
            <div>
              <p className="text-sm font-medium text-gray-600">Significant Results</p>
              <p className="text-2xl font-bold text-gray-900">{formatNumber(insights.stats.statistically_significant)}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
          <div className="flex items-center">
            <Zap className="h-8 w-8 text-orange-600 mr-3" />
            <div>
              <p className="text-sm font-medium text-gray-600">Quality Score</p>
              <p className="text-2xl font-bold text-gray-900">{insights.data_quality.quality_metrics.overall_score}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Analysis Type Chips */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <h3 className="text-lg font-semibold text-gray-900 mb-3">Analysis Results</h3>
        <div className="flex flex-wrap gap-3">
          <div className="flex items-center bg-blue-50 border border-blue-200 rounded-full px-3 py-1">
            <span className="text-sm font-medium text-blue-800">Burst Correlations</span>
            <span className="ml-2 bg-blue-200 text-blue-800 text-xs font-bold rounded-full px-2 py-0.5">
              {insights.stats.burst_pairs_count}
            </span>
          </div>

          <div className="flex items-center bg-green-50 border border-green-200 rounded-full px-3 py-1">
            <span className="text-sm font-medium text-green-800">Lead-Lag</span>
            <span className="ml-2 bg-green-200 text-green-800 text-xs font-bold rounded-full px-2 py-0.5">
              {insights.stats.lead_lag_count}
            </span>
          </div>

          <div className="flex items-center bg-purple-50 border border-purple-200 rounded-full px-3 py-1">
            <span className="text-sm font-medium text-purple-800">PMI Co-occurrence</span>
            <span className="ml-2 bg-purple-200 text-purple-800 text-xs font-bold rounded-full px-2 py-0.5">
              {insights.stats.pmi_count}
            </span>
          </div>

          <div className="flex items-center bg-orange-50 border border-orange-200 rounded-full px-3 py-1">
            <span className="text-sm font-medium text-orange-800">Change Attribution</span>
            <span className="ml-2 bg-orange-200 text-orange-800 text-xs font-bold rounded-full px-2 py-0.5">
              {insights.stats.change_attribution_count}
            </span>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <div className="border-b border-gray-200">
          <nav className="flex space-x-8 px-6">
            <button
              onClick={() => setActiveTab('anomalies')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'anomalies'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Top Anomalies ({insights.top_anomalies.length})
            </button>
            <button
              onClick={() => setActiveTab('correlations')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'correlations'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Correlations ({insights.correlations.length})
            </button>
            <button
              onClick={() => setActiveTab('drift')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'drift'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Drift & Quality
            </button>
          </nav>
        </div>

        <div className="p-6">
          {activeTab === 'anomalies' && <AnomaliesTable anomalies={insights.top_anomalies} />}
          {activeTab === 'correlations' && (
            <CorrelationsPanel 
              burstPairs={insights.burst_pairs}
              leadLag={insights.lead_lag}
              pmi={insights.pmi}
            />
          )}
          {activeTab === 'drift' && (
            <DriftQualityPanel 
              dataQuality={insights.data_quality}
              severityContext={insights.severity_context}
              adaptiveThresholds={insights.adaptive_thresholds}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default InsightsDashboard;
