import React, { useState } from "react";
import { Insights } from "../types/insights";
import {
  AlertTriangle,
  Activity,
  TrendingUp,
  Database,
  Clock,
  Zap,
} from "lucide-react";
import AnomaliesTable from "./AnomaliesTable";
import CorrelationsPanel from "./CorrelationsPanel";
import DriftQualityPanel from "./DriftQualityPanel";

interface InsightsDashboardProps {
  insights: Insights | null;
  loading: boolean;
  error: string | null;
}

const InsightsDashboard: React.FC<InsightsDashboardProps> = ({
  insights,
  loading,
  error,
}) => {
  const [activeTab, setActiveTab] = useState<
    "anomalies" | "correlations" | "drift"
  >("anomalies");

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        <span className="ml-3 text-lg">Loading insights...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-4">
        <div className="flex items-center">
          <AlertTriangle className="h-5 w-5 text-red-600 mr-2" />
          <span className="text-red-800">Error loading insights: {error}</span>
        </div>
      </div>
    );
  }

  if (!insights) {
    return (
      <div className="bg-gray-50 border border-gray-200 rounded-lg p-8 text-center">
        <Database className="h-12 w-12 text-gray-400 mx-auto mb-4" />
        <h3 className="text-lg font-medium text-gray-900 mb-2">
          No Insights Available
        </h3>
        <p className="text-gray-600">
          Run the anomaly engine to generate insights.
        </p>
      </div>
    );
  }

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "critical":
        return "bg-red-100 text-red-800 border-red-200";
      case "high":
        return "bg-orange-100 text-orange-800 border-orange-200";
      case "medium":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "low":
        return "bg-blue-100 text-blue-800 border-blue-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  // const getContextColor = (context: string) => {
  //   switch (context) {
  //     case "high_error_environment":
  //       return "text-red-600";
  //     case "medium_error_environment":
  //       return "text-orange-600";
  //     case "low_medium_error_environment":
  //       return "text-yellow-600";
  //     case "low_error_environment":
  //       return "text-green-600";
  //     default:
  //       return "text-gray-600";
  //   }
  // };

  const formatNumber = (num: number, decimals: number = 0) => {
    return new Intl.NumberFormat("en-US", {
      minimumFractionDigits: decimals,
      maximumFractionDigits: decimals,
    }).format(num);
  };

  // const formatPercentage = (num: number) => {
  //   return `${(num * 100).toFixed(1)}%`;
  // };

  return (
    <div className="space-y-6">
